function typeText (){
let type = document.querySelector('.type')
let text = type.innerHTML
let out = ''
let i = 0
function typeLine (){
 let interval = setTimeout (function(){
 out += text[i]
 type.innerHTML = out
 i++
 if (i >= text.length) {
 clearTimeout (interval)
 return true
 }
 typeLine()
 }, 90)
}
typeLine()
}
